query DayDishesQuery($dayOfWeek: DayOfWeek!, $timezoneOffset: Int, $cityId: Int) {
  day(dayOfWeek: $dayOfWeek, timezoneOffset: $timezoneOffset, cityId: $cityId) {
    isFreeOfCharge
    comment
    orderUntil
    providers {
      id
      name
      color
      categories {
        id
        title
        selections {
          id
          price
          maxCount
          requestsCount
          isMultipleSelection
          isTopPick
          dishes {
            id
            title
            isSoup
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
